mapper.update:
update areas
set areas_name = #{areasName,jdbcType=VARCHAR},
  areas_price = #{areasPrice,jdbcType=DECIMAL},
  areas_remarks = #{areasRemarks,jdbcType=VARCHAR},
  city_id = #{cityId,jdbcType=VARCHAR}
where areas_id = #{areasId,jdbcType=INTEGER}

mapper.select:
select areas_id, areas_name, areas_price, areas_remarks, city_id
from areas
where areas_id = #{areasId,jdbcType=INTEGER}

